mapper.select:
select * from mac_vod
where vod_name = #{vodName} 
and vod_director = #{vodDirector}

select * from mac_vod
where (vod_actor = #{actor} and #{actor} is not null and #{actor} != '')
or (vod_director = #{director} and #{director} is not null and #{director} != '') 
or type_id = #{typeId}  
order by last_time desc 
limit #{page},#{limit}

select * from mac_vod where 1=1

	and vodName=#{vodName}


	and typeId=#{typeId}


	and typeId1=#{typeId1}


	and groupId=#{groupId}

 order by vod_time_add desc

	limit #{page},#{limit}

select count(*) from mac_vod where 1=1

	and vodName=#{vodName}


	and typeId=#{typeId}


	and typeId1=#{typeId1}


	and groupId=#{groupId}

select * from mac_vod where vod_sub=1

	and vod_tv=#{vodTv}

  order by tv_serial_number asc

	limit #{page},#{limit}

select * from mac_vod where 1=1

	and vod_tv=#{vodTv}


	and tv_serial_number=#{tvSerialNumber}


	limit #{page},#{limit}
